$(document).ready(function(a) {

    var contact = $(".contact-fields");

    contact.submit(function(event) {
        event.preventDefault();
        var data = {
            name: $('.name').val(),
            email: $('.email').val(),
            message: $('.message').val()
        };
        $.ajax({
            type: "POST",
            url: "process.php",
            data: data,
            success: function (response) {
                contact.html('<div class="valign-wrapper"><div class="email-message col s12 valign center-align wow fadeIn"><h5>Your email was sent!</h5>'+ 'I will get in touch with you soon.' + "</div></div>"), Materialize.toast("Email sent!", 8e3);
            },
            error: function() {
                console.log('error');
            }
        });
    });
});
